changeSet.sql:
CREATE OR REPLACE FUNCTION public.compare_as_numbers_or_strings(in_1 text, in_2 text)
 RETURNS boolean
 LANGUAGE plpgsql
AS $function$
            begin
    if textregexeq($1,'^-?[[:digit:]]+(\.[[:digit:]]+)?$')
            and textregexeq($2,'^-?[[:digit:]]+(\.[[:digit:]]+)?$')
            then return $1::double precision = $2::double precision;
            else return $1 = $2;
            end if;
            end;
            $function$
            ;
